const languages = require("../data/languages.json")

const items = [
  { longCode: "auto", name: "automatic language detection" },
  ...languages,
]
  .filter(
    (language) =>
      !["ca-ES-valencia", "de-DE-x-simple-language"].includes(
        language.longCode,
      ),
  )
  .map(
    (language) =>
      `      <li><tt style="white-space: pre;">${language.longCode.padEnd(
        8,
      )}</tt> - ${language.name}</li>`,
  )
  .join("\n")

console.log(items)
